import React, { useState } from 'react';
import axios from 'axios';

const ReportBug = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Adjust the URL to your backend API endpoint
      await axios.post('http://localhost:5000/api/bugs', { title, description });
      setMessage('Bug reported successfully!');
      setTitle('');
      setDescription('');
    } catch (error) {
      console.error('Error reporting bug:', error);
      setMessage('Failed to report bug. Please try again later.');
    }
  };

  return (
    <div className="max-w-md mx-auto p-6 bg-gray-800 text-white rounded-lg">
      <h2 className="text-2xl mb-4">Report a Bug</h2>

      {message && <p className="mb-4">{message}</p>}

      <form onSubmit={handleSubmit} className="flex flex-col gap-4">
        <label>
          Bug Title:
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
            className="w-full p-2 rounded text-black"
          />
        </label>

        <label>
          Description:
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
            className="w-full p-2 rounded text-black"
            rows={5}
          />
        </label>

        <button
          type="submit"
          className="bg-green-600 py-2 rounded hover:bg-green-700 transition"
        >
          Submit Bug Report
        </button>
      </form>
    </div>
  );
};

export default ReportBug;
