import React from 'react';
import './styles.css';

function App() {
  const handleViewBugs = () => {
    alert("Navigating to bug list...");
  };

  const handleReportBug = () => {
    alert("Navigating to report form...");
  };

  const handleAbout = () => {
    alert("This is a MERN Testing App for Week 6 & 7 DevOps!");
  };

  return (
    <div className="app-container">
      <h1 className="title">üêû MERN Bug Tracker</h1>
      <p className="subtitle">Built with MongoDB, Express, React, and Node.js</p>

      <div className="button-group">
        <button onClick={handleViewBugs}>View Bugs</button>
        <button onClick={handleReportBug}>Report New Bug</button>
        <button onClick={handleAbout}>About</button>
      </div>
    </div>
  );
}

export default App;
